/* IMPORT */
import Command from './command.js';
/* MAIN */
class CommandVersion extends Command {
    /* CONSTRUCTOR */
    constructor(bin) {
        super(bin, {
            name: '_version',
            description: 'Display the version number',
            hidden: true
        });
    }
    /* API */
    async run(options, argv) {
        this.logger.print(this.bin.metadata.version);
    }
}
/* EXPORT */
export default CommandVersion;
