/* IMPORT */
import Bin from './objects/bin.js';
import ChainableBinGlobal from './objects/chainable_bin_global.js';
/* MAIN */
const bin = (name, description) => {
    const bin = new Bin({ name, description });
    const chainable = new ChainableBinGlobal(bin);
    return chainable;
};
/* EXPORT */
export default bin;
