declare const isBoolean: (value: unknown) => value is boolean;
declare const isInteger: (value: unknown) => value is number;
declare const isObject: (value: unknown) => value is object;
declare const isObjectEmpty: (value: object) => boolean;
declare const isString: (value: unknown) => value is string;
declare const isUndefined: (value: unknown) => value is undefined;
export { isBoolean, isInteger, isObject, isObjectEmpty, isString, isUndefined };
