/* IMPORT */
import colors from 'tiny-colors';
import Addon from './addon.js';
/* MAIN */
class Metadata extends Addon {
    constructor() {
        /* VARIABLES */
        super(...arguments);
        this.name = 'bin';
        this.description = '';
        this.package = '';
        this.version = '0.0.0';
        this.colors = true;
        this.exiter = true;
        this.updater = true;
    }
    /* API */
    print() {
        this.logger.print(`${colors.cyan(this.name)} ${colors.dim(this.version)}`);
        this.logger.print();
    }
}
/* EXPORT */
export default Metadata;
